<?phpadd_action( 'after_setup_theme', 'metoob_theme_setup' );$includes_path = TEMPLATEPATH . '/includes/';require_once ($includes_path . 'theme-options.php');function metoob_theme_setup() {        add_theme_support( 'post-thumbnails' );        add_action( 'init', 'metoob_register_menus' );    add_action( 'init', 'metoob_register_sidebars' );    remove_filter('the_excerpt', 'wpautop');    }function metoob_register_sidebars() {        if ( function_exists('register_sidebar') ) {        register_sidebar();    }    }function metoob_register_menus() {    	register_nav_menus(		array(			'header-menu' => __( 'Header Menu' ),			'footer-menu' => __( 'Footer Menu' )		)	);    }function metoob_comment($comment, $args, $depth) {   $GLOBALS['comment'] = $comment; ?>   <li <?php comment_class(); ?> id="li-comment-<?php comment_ID() ?>">     <div id="comment-<?php comment_ID(); ?>" class="clearfix">      <div class="comment-author vcard">         <?php printf(__('<cite class="fn">%s</cite>:&nbsp;'), get_comment_author_link()) ?>      </div>      <?php if ($comment->comment_approved == '0') : ?>         <em><?php _e('Your comment is awaiting moderation.') ?></em>         <br />      <?php endif; ?>               <?php comment_text() ?>      <?php                $todaysDate = strtotime( date("Y-m-d") );        $commentDate = strtotime( get_comment_date() );        $nowTime = strtotime( date( "H:m:s" ) );        $commentTime = strtotime( get_comment_time() );        $elapsedTime = $todaysDate - $commentDate < 86400 ? abs($nowTime - $commentTime) : abs($todaysDate - $commentDate);                $years = floor($elapsedTime / (365*60*60*24));        $months = floor(($elapsedTime - $years * 365*60*60*24) / (30*60*60*24));        $days = floor(($elapsedTime - $years * 365*60*60*24 - $months*30*60*60*24)/ (60*60*24));        $hours = floor( $elapsedTime / ( 60 * 60 ) );        $minutes = floor( $elapsedTime / 60 );                if ( $elapsedTime < 3600 ) {            // Show minutes since comment            $elapsedTimeStr = $minutes . ( $minutes == 1 ? " minute ago" : " minutes ago" );        } else if ( $elapsedTime < 86400 ) {            // Show hours since comment            $elapsedTimeStr = $hours . ( $hours == 1 ? " hour ago" : " hours ago" );        } else if ( $elapsedTime < 2592000 ) {            // Show days since comment            $elapsedTimeStr = $days . ( $days == 1 ? " day ago" : " days ago" );        } else if ( $elapsedTime < 31104000) {            // Show months since comment            $elapsedTimeStr = $months . ( $months == 1 ? " month ago" : " months ago" );        } else {            // Show years since comment            $elapsedTimeStr = $years . ( $years == 1 ? " year ago" : " years ago" );        }              ?>               <div class="comment-meta commentmetadata">        <?php echo $elapsedTimeStr ?>        <?php edit_comment_link(__('[ Edit ]'),'  ','') ?>      </div>      <div class="reply">         <?php comment_reply_link(array_merge( $args, array('depth' => $depth, 'max_depth' => $args['max_depth']))) ?>      </div>     </div><?php        }/*    DEBUGB Functions*/if( !function_exists( '_log' ) ) {        function _log( $message ) {            if( WP_DEBUG === true ) {                        if( is_array( $message ) || is_object( $message ) ) {                error_log( print_r( $message, true ) );            } else {                error_log( $message );            }                    }            }}        ?>