<?php /** * The loop that displays a single post. * * The loop displays the posts and the post content.  See * http://codex.wordpress.org/The_Loop to understand it and * http://codex.wordpress.org/Template_Tags to understand * the tags used in it. * * This can be overridden in child themes with loop-single.php. * * @package WordPress * @subpackage MeToob * @since MeToob 2.0 */ ?><?php if ( have_posts() ) while ( have_posts() ) : the_post(); ?><article id="main-video" class="clearfix">    <input type="hidden" id="current-post-id-hidden" value="<?php the_ID(); ?>" />    <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>        <h1 class="entry-title"><?php the_title(); ?></h1>        <div class="entry-content">            <?php the_content(); ?>        </div>        <?php if ( 'post' == get_post_type() ) { ?>        <div id="share-buttons">            <a id="send-to-friend-btn" title="Send this video to a friend..." href="#"><img height="25" width="117" alt="Send this video to a friend..." src="<?php echo get_bloginfo( 'url' ); ?>/wp-content/themes/metoob/images/button_SendToFriend.gif"></a>        </div>        <?php } ?>    </div><!-- #post-## -->    <?php if ( 'post' == get_post_type() ) { ?>    <div id="comments">        <?php comments_template( '', true ); ?>    </div>    <?php } ?></article><div id="send-to-friend-form" class="form" style="display:none;">    <form action="#" method="post">        <div class="field-label-pair">            <label for="email-to">Your friend's email address (separate multiples with a comma)</label>            <input autocomplete="on" type="text" id="email-to" name="email-to" />        </div>        <div class="field-label-pair">            <label for="email">Your email address (required)</label>            <input autocomplete="on" type="text" id="email" name="email" />        </div>        <div class="preview clearfix">            <p>Your friend at [Email Address Here] has sent you this informational video&hellip;</p>            <div class="listing clearfix selected">                <div class="thumbnail">                    <?php $thumb = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' ) ?>                    <a href="<?php the_permalink(); ?>" title="Play" onclick="return false;"><img src="<?php echo count( $thumb ) > 0 ? $thumb[0] : 'No thumbnail'; ?>" width="100" height="56" alt="Play" /></a>                    <a href="<?php the_permalink(); ?>" title="Play" onclick="return false;"><img class="playButton" src="<?php echo get_bloginfo( 'url' ); ?>/wp-content/themes/metoob/images/button_Play-thumbnail.png" width="26" height="26" alt="Play" /></a>                </div>                <div class="description">                    <h4><a href="<?php the_permalink(); ?>" onclick="return false;"><?php the_title(); ?> (<?php echo get_post_meta($post->ID, 'Runtime', true); ?>)</a></h4>                    <p><?php echo the_excerpt(); ?>&hellip;</p>                </div>            </div>            <p>Click the play button above or click the following link to view this video.<p>            <p><a href="<?php the_permalink(); ?>" onclick="return false;"><?php the_permalink(); ?></a></p>        </div>    </form></div><?php endwhile; ?>