<nav id="video-library">    <h3>Video Library</h3>    <ul>    <?php        $posts_array = get_posts( 'numberposts=10' );        foreach( $posts_array as $post ) : setup_postdata( $post ); ?>            <li>                <div id="listing-<?php echo $post->ID ?>" class="listing clearfix " onclick="window.location.href='<?php the_permalink(); ?>';">                    <div class="thumbnail">                        <?php $thumb = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' ) ?>                        <a href="<?php the_permalink(); ?>" title="Play"><img src="<?php echo count( $thumb ) > 0 ? $thumb[0] : 'No thumbnail'; ?>" width="100" height="56" alt="Play" /></a>                        <a href="<?php the_permalink(); ?>" title="Play"><img class="playButton" src="<?php echo get_bloginfo( 'url' ); ?>/wp-content/themes/metoob/images/button_Play-thumbnail.png" width="26" height="26" alt="Play" /></a>                    </div>                    <div class="description">                        <h4><a href="<?php the_permalink(); ?>"><?php the_title(); ?> (<?php echo get_post_meta($post->ID, 'Runtime', true); ?>)</a></h4>                        <p><?php echo the_excerpt(); ?>&hellip;</p>                    </div>                </div>            </li>        <?php endforeach; ?>    </ul></nav><div id="sidebar">    <?php    if ( function_exists( 'dynamic_sidebar' ) ) {        dynamic_sidebar();    }    ?></div>